INSERT INTO `ai_supports` (`id`, `program_id`, `ai_support_text`, `ai_support_text_de`) VALUES
(1, 1, '<h2><strong>Smarter studying with powerful AI support</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>Studying without AI? Not at ONSITES.</p>\r\n\r\n<p>At ONSITES Graduate School, artificial intelligence doesn&rsquo;t just complement your study process &ndash; it makes it easier.</p>\r\n\r\n<p>Texts are written faster, language barriers fade, and questions about your study content are answered immediately.</p>\r\n\r\n<p>Three smart AI tools support you &ndash; tailored to the challenges you face during your studies.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>AI Writing Assistant<sup>Beta</sup></strong></p>\r\n\r\n	<p>Research &amp; study with smart precision: Faster and better with AI-optimized texts.</p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;<strong>AI Translator</strong></p>\r\n\r\n	<p>Communication without borders: Real-time translation of texts and conversations &ndash; globally connected.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>AI Study Mentor<sup>Beta</sup></strong></p>\r\n\r\n	<p>24/7 AI Study Mentor: Instant answers to all your questions about the course content</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', '<h2><strong>Clever studieren mit smarter KI-Power an Deiner Seite</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Studieren ohne KI? Nicht bei ONSITES.</p>\r\n\r\n<p>An der ONSITES Graduate School erg&auml;nzt k&uuml;nstliche Intelligenz nicht einfach den Studienprozess &ndash; sie erleichtert ihn.</p>\r\n\r\n<p>Texte entstehen schneller, Sprachbarrieren verschwinden und Fragen zu Studieninhalten werden sofort beantwortet.</p>\r\n\r\n<p>Drei smarte KI-Tools unterst&uuml;tzen Dich dabei &ndash; abgestimmt auf Deine Herausforderungen im Studium.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>KI-Schreibassistent<sup>Beta</sup></strong></p>\r\n\r\n	<p>Forschen &amp; studieren mit smarter Pr&auml;zision: Schneller und besser durch KI-optimierte Texte</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>KI-&Uuml;bersetzer</strong></p>\r\n\r\n	<p>Grenzenlose Kommunikation: Echtzeit-&Uuml;bersetzung von Texten &amp; Gespr&auml;chen &ndash; global vernetzt</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>KI-Studienmentor<sup>Beta</sup></strong></p>\r\n\r\n	<p>24/7 KI-Studienmentor: Sofort-Antworten auf alle Fragen zu Studieninhalten.</p>\r\n	</li>\r\n</ul>'),
(2, 15, '<p>asdfdas123</p>', '<p>fdasfdas</p>');
