INSERT INTO `career_paths` (`id`, `text`, `text_de`, `program_id`) VALUES
(1, '<h2><strong>Bachelor Professional: Unlock your Potential &amp; Power your Career</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">With the Bachelor Professional in Business, a wide range of career opportunities opens up for you across key sectors of the economy. Whether in procurement, finance and accounting, or human resources &ndash; holders of the Bachelor Professional in Business take on responsibility, lead teams, and actively shape business processes. They are in demand across industries &ndash; from small and medium-sized enterprises to international corporations. With your qualification, you position yourself as a strategic-minded business all-rounder &ndash; ideally suited for demanding roles in operational management, staff units, or as an interface between departments and executive leadership. Discover diverse career opportunities &ndash; and shape the future of business with your expertise.</p>', '<h2><strong>Karriere f&uuml;r Wirtschaftsfachwirte: Potenziale entfalten &amp; durchstarten</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Mit dem Abschluss zum Wirtschaftsfachwirt &ndash; Bachelor Professional in Business er&ouml;ffnen sich Dir vielf&auml;ltige Karrierewege in einer Vielzahl von Schl&uuml;sselbereichen der Wirtschaft. Ob im Einkauf, im Finanz- und Rechnungswesen oder im Personalmanagement &ndash; Wirtschaftsfachwirte &uuml;bernehmen Verantwortung, leiten Teams und gestalten unternehmerische Prozesse aktiv mit. Wirtschaftsfachwirte sind branchen&uuml;bergreifend gefragt &ndash; in mittelst&auml;ndischen Unternehmen ebenso wie in internationalen Konzernen. Mit Deinem Abschluss positionierst Du Dich als betriebswirtschaftlicher Allrounder mit strategischem Weitblick &ndash; ideal f&uuml;r anspruchsvolle Rollen im operativen Management, in Stabsstellen oder als Schnittstelle zwischen Fachabteilungen und Unternehmensleitung. Entdecke jetzt vielf&auml;ltige Karriereoptionen und gestalte die Wirtschaft mit Deinem Know-how.</p>', 1),
(2, '<p>asdfadsdasfasdfadsf</p>', '<p>sdfdasfdasfdsf</p>', 15);
