INSERT INTO `dynamic_news_authors_translations` (`id`, `locale`, `description`, `author_id`, `name`, `avatar`, `slug`, `deleted_at`) VALUES
(1, 'en', 'Elitsa is a seasoned content creator, holding a Bachelor’s degree in Journalism from the University of Westminster. A writer by day and a reader by night, she loves to delve into different worlds of writing, expanding her professional horizons while drawing inspiration from diverse themes and topics.', 1, 'Elitsa Grigorova', 'avatar-eli.webp', 'elitsa-grigorova', NULL),
(2, 'de', 'Elitsa ist eine erfahrene Content-Creatorin mit einem Bachelorabschluss in Journalismus von der University of Westminster. Als Texterin am Tag und begeisterte Leserin am Abend liebt sie es, in verschiedene Schreibwelten einzutauchen, ihren beruflichen Horizont zu erweitern und sich von verschiedenen Themen inspirieren zu lassen.', 1, 'Elitsa Grigorova', 'avatar-eli.webp', 'elitsa-grigorova', NULL),
(3, 'en', 'Kalina, a dedicated Content Writer, transformed her love for writing from a personal blog into a thriving career. With a knack for crafting engaging long and short-form content, she brings passion and expertise to every piece. Explore more of Kalina\'s versatile writing on our website.', 2, 'Kalina Georgieva', 'avatar-kali.webp', 'kalina-georgieva', NULL),
(4, 'de', 'Kalina, eine engagierte Content-Writerin, hat ihre Liebe zum Schreiben von einem persönlichen Blog in eine blühende Karriere verwandelt. Mit ihrem Talent für die Erstellung von ansprechenden Beiträgen bringt sie Leidenschaft und Fachwissen in jeden Text ein. Entdecke mehr von Kalinas vielseitiger Arbeit auf unserer Website.', 2, 'Kalina Georgieva', 'avatar-kali.webp', 'kalina-georgieva', NULL),
(5, 'en', 'Prof. Dr. Mathias Kunze is an economist, entrepreneur and business consultant with over 35 years of experience in business, marketing and law. He is the founder and CEO of the ONSITES Group, develops international web portals in the education sector and teaches at the ONSITES Graduate School and international universities. Prof. Dr. Kunze has authored many studies and articles and received the President\'s Volunteer Service Award for his commitment. He speaks German, English, Polish and Russian.', 3, 'Prof. Dr. Mathias Kunze', 'mathias-kunze.webp', 'mathias-kunze', NULL),
(6, 'de', 'Prof. Dr. Mathias Kunze ist Wirtschaftswissenschaftler, Entrepreneur und Business Consultant mit über 35 Jahren Erfahrung in Business, Marketing und Recht. Er ist Gründer und CEO der ONSITES Group, entwickelt internationale Webportale im Bildungsbereich und lehrt an der ONSITES Graduate School sowie internationalen Universitäten. Prof. Dr. Kunze hat viele Studien und Artikel verfasst und erhielt für sein Engagement den President’s Volunteer Service Award. Er spricht Deutsch, Englisch, Polnisch und Russisch.', 3, 'Prof. Dr. Mathias Kunze', 'mathias-kunze.webp', 'mathias-kunze', NULL);
