INSERT INTO `faq_categories_translation` (`id`, `category_id`, `slug`, `name`, `locale`, `meta_title`, `meta_description`) VALUES
(1, 1, 'onsites-graduate-school', 'About Us', 'en', 'FAQ - About Us | ONSITES Graduate School', 'Get all the answers about ONSITES Graduate School. Your path to success in higher education & research. Click now for more!'),
(3, 2, 'studies-courses', 'Study Programs', 'en', 'FAQ - Studies & Courses | ONSITES Graduate School', 'Find comprehensive answers to FAQ about ONSITES Graduate School’s Studies & Courses. Unlock the insights you need for a successful academic journey. Click Now!'),
(5, 3, 'doctoral-degree', 'Doctorate', 'en', 'FAQ -  Doctorate | ONSITES Graduate School', 'Find answers to your questions about pursuing a Doctorate at ONSITES Graduate School. Learn about Program structure, examinations, duration & more. Read more!'),
(7, 4, 'events', 'Conferences & Workshops                ', 'en', 'FAQ - Conferences & Workshops | ONSITES Graduate School', 'Find answers to your queries on ONSITES Graduate School\'s Events. Opportunities for networking, professional growth & more. Read. Start now!'),
(11, 6, 'coaching', 'Coaching', 'en', 'FAQ -  Coaching | ONSITES Graduate School', 'Discover in-depth answers to your queries about Coaching at ONSITES Graduate School. Find clarity on types of coaching, benefits & more. Click here.'),
(15, 5, 'publication', 'Publishing', 'en', 'FAQ -  Publishing | ONSITES Graduate School', 'Any questions about publishing a paper? Our FAQ page got you covered. Everything at a glance. Click now and unlock publishing success.');
INSERT INTO `faq_translations` (`id`, `faq_id`, `question`, `answer`, `locale`) VALUES
(3, 116, 'Who is ONSITES Graduate School?', '<p>ONSITES Graduate School is an private education institution offering job-accompanying <a href=\"https://graduate.me/en/bachelor-degree\">Bachelor</a> degrees, <a href=\"https://graduate.me/en/master-degree\">Master</a> degrees, <a href=\"https://graduate.me/en/mba\">MBA </a>degrees, and&nbsp;<a href=\"https://graduate.me/en/doctorate\">doctorates</a>.&nbsp;Its service portfolio is complemented by&nbsp;<a href=\"https://graduate.me/en/conferences-workshops\">conferences &amp; workshops</a>, <a href=\"https://graduate.me/en/coaching-services\">coachings</a>, and <a href=\"https://graduate.me/en/book-publishing\">publishing</a>&nbsp;services.</p>', 'en'),
(4, 116, 'Wer ist die ONSITES Graduate School?', '<p>Die ONSITES Graduate School ist eine private Bildungsinstitution, die berufsbegleitende <a href=\"https://graduate.me/de/bachelor\">Bachelorstudieng&auml;nge</a>, <a href=\"https://graduate.me/de/master\">Masterstudieng&auml;nge</a>, <a href=\"https://graduate.me/de/mba\">MBA-Programme</a>&nbsp;sowie&nbsp;<a href=\"https://graduate.me/de/doktorat\">Doktorate</a>&nbsp;anbietet. Erg&auml;nzt wird ihr&nbsp;Leistungsportfolio durch&nbsp;<a href=\"https://graduate.me/de/konferenzen-und-workshops\">Konferenzen und Workshops</a>, <a href=\"https://graduate.me/de/coaching\">Coachings</a> sowie <a href=\"https://graduate.me/de/buch-veroeffentlichen\">Ver&ouml;ffentlichungen</a>.</p>', 'de'),
(11, 122, 'Where is ONSITES Graduate School located?', '<p>Headquartered in<a href=\"https://graduate.me/en/blog/st-petersburg-florida\"> St. Petersburg </a>(Florida, USA), ONSITES Graduate School is also represented internationally with branches in Zurich (Switzerland) and Varna (Bulgaria).</p>', 'en'),
(12, 122, 'Wo befindet sich die ONSITES Graduate School?', '<p>Mit <a href=\"https://graduate.me/de/blog/st-petersburg-florida\">Hauptsitz in St. Petersburg</a> (Florida, USA) ist die ONSITES Graduate School zudem mit Niederlassungen in Z&uuml;rich (Schweiz) und Varna (Bulgarien) international vertreten.</p>', 'de'),
(13, 123, 'Who does the faculty of ONSITES Graduate School include?', '<p>The <a href=\"https://graduate.me/en/lecturers\">faculty</a> at ONSITES Graduate School combines experts from business and industry with academically highly qualified lecturers who hold a doctorate, habilitation or professorship.</p>', 'en'),
(14, 123, 'Wer gehört zum Lehrkörper der ONSITES Graduate School?', '<p>Der <a href=\"https://graduate.me/de/dozenten\">Lehrk&ouml;rper</a> der ONSITES Graduate School vereint Experten&nbsp;aus Wirtschaft und Industrie mit akademisch hochqualifizierten Lehrenden, die promoviert oder habilitiert sind bzw. eine Professur innehaben.</p>', 'de'),
(15, 124, 'How many students does a tutor supervise at ONSITES Graduate School?', '<p>A tutor at ONSITES Graduate School supervises an average of fifteen students, thus ensuring proper support for all.</p>', 'en'),
(16, 124, 'Wie viele Studierende werden pro Tutor betreut?', '<p>Ein Tutor betreut im Durchschnitt 15 Studierende, so dass eine angemessene Unterst&uuml;tzung f&uuml;r alle gew&auml;hrleistet ist.</p>', 'de'),
(17, 125, 'What universities does ONSITES Graduate School work in partnership with?', '<p>As part of its international network, ONSITES Graduate School cooperates with state-recognized and accredited universities in Europe and Asia. <a href=\"https://graduate.me/en/accreditation-partners\">Partner institutions</a> include the IIC University of Technology, the Royal University of Phnom Penh (RUPP), as well as the European Polytechnical University Pernik (EPUP).</p>', 'en'),
(18, 125, 'Mit welchen Hochschulen kooperiert die ONSITES Graduate School?', '<p>Im Rahmen ihres internationalen Netzwerks kooperiert die ONSITES Graduate School mit staatlich anerkannten und akkreditierten Hochschulen in Europa und Asien. Zu den <a href=\"https://graduate.me/de/akkreditierung-und-partner\">Partnerinstitutionen</a>&nbsp;z&auml;hlen unter anderem die IIC University of Technology, die Royal University of Phnom Penh (RUPP) sowie&nbsp;die European Polytechnical University Pernik (EPUP).</p>', 'de'),
(19, 126, 'Why should I pursue a degree?', '<p>In contrast to traditional vocational training, a degree offers a wider range of opportunities and more diverse job opportunities. It serves as a catalyst for personal career growth, providing better job prospects, even during economically challenging times.</p>', 'en'),
(20, 126, 'Warum ist ein Studium sinnvoll?', '<p>Im Gegensatz zur traditionellen Berufsausbildung bietet ein Hochschulabschluss ein breiteres Spektrum an M&ouml;glichkeiten und vielf&auml;ltigere berufliche Perspektiven. Es dient als Katalysator f&uuml;r die pers&ouml;nliche Karriereentwicklung und bietet bessere Berufschanchen, auch in wirtschaftlich schwierigen Zeiten.</p>', 'de'),
(21, 127, 'Why take an online course at ONSITES Graduate School?', '<p>At ONSITES Graduate School, you only learn what you really need. The application-oriented education ensures that you can apply what you have learned directly to real professional situations. You receive personal and comprehensive support throughout your online course of study and benefit from maximum flexibility thanks to state-of-the-art study concepts.</p>', 'en'),
(22, 127, 'Was spricht für ein Online-Studium an der ONSITES Graduate School?', '<p>An der ONSITES Graduate School lernst Du nur das, was Du wirklich brauchst. Die anwendungsorientierte Lehre gew&auml;hrleistet, dass Du das Gelernte direkt auf reale berufliche Situationen anwenden kannst. W&auml;hrend des gesamten Online-Studiums wirst Du pers&ouml;nlich und umfassend betreut und profitierst von maximaler Flexibilit&auml;t dank hochmoderner Studienkonzepte.</p>', 'de'),
(23, 128, 'Are the study programs at ONSITES Graduate School accredited?', '<p>Absolutely. The accreditation of our degree programs is guaranteed by our cooperation with <a href=\"https://graduate.me/en/accreditation-partners\">accredited</a>&nbsp;partners in Germany and abroad.</p>', 'en'),
(24, 128, 'Sind die Studiengänge an der ONSITES Graduate School staatlich anerkannt bzw. akkreditiert?', '<p>Unbedingt. Die Akkreditierung unserer Studieng&auml;nge ist gew&auml;hrleistet durch unsere Zusammenarbeit mit <a href=\"https://graduate.me/de/akkreditierung-und-partner\">akkreditierten</a>&nbsp;Kooperationspartnern im In- und Ausland.</p>', 'de'),
(25, 129, 'Does ONSITES Graduate School allow studying without a school-based higher education entrance qualification?', '<p>Yes, ONSITES Graduate School allows studying without a school-based higher education entrance qualification for individuals who have completed vocational training and have several years of professional experience or who have completed vocational training equivalent to the Abitur (e.g., Business Administrator, Certified Business Economist, etc.).</p>', 'en'),
(26, 129, 'Studieren ohne Abitur – geht das?', '<p>Ja. Die ONSITES Graduate School erm&ouml;glicht ein Studieren ohne Abitur bzw. Fachabitur f&uuml;r Personen mit abgeschlossener Berufsausbildung und mehrj&auml;hriger Berufserfahrung bzw. einer dem Abitur &auml;quivalenten beruflichen Fortbildung (z.B. Fachwirt, staatlich gepr&uuml;fter Betriebswirt).</p>', 'de'),
(27, 130, 'What fields of study are available at ONSITES Graduate School?', '<p>ONSITES Graduate School offers a diverse and future-oriented range of courses in key areas such as business &amp; management, engineering &amp;&nbsp;information technology, law, and health sciences.</p>', 'en'),
(28, 130, 'Welche Studienrichtungen gibt es an der ONSITES Graduate School?', '<p>Die ONSITES Graduate School bietet ein vielf&auml;ltiges und zukunftsorientiertes Studienangebot in Schl&uuml;sselbereichen wie Wirtschaftswissenschaften, Ingenieurwissenschaften und Informationstechnologie, Rechtswissenschaften sowie Gesundheitswissenschaften.</p>', 'de'),
(29, 131, 'What types of degrees are offered at ONSITES Graduate School?', '<p>In close cooperation with our internationally recognized partner universities, ONSITES Graduate School offers a wide range of study programs at <a href=\"https://graduate.me/en/bachelor-degree\">Bachelor</a>, <a href=\"https://graduate.me/en/master-degree\">Master</a>, <a href=\"https://graduate.me/en/mba\">MBA</a> and <a href=\"https://graduate.me/en/doctorate\">doctorate</a> level.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>At Bachelor level, students can choose between the Bachelor of Business Administration (Fast Track) and the Bachelor Professional in Business (Wirtschaftsfachwirt).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>At Master&#39;s level, programs such as the Master of IT Management, Master of Cybersecurity, Master of Green Energetics, Master of Laws as well as the Master of Health Sciences and the Master Professional in Business (Betriebswirt) are available.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>The Executive Master of Business Administration (MBA) and the MBA Professional in Artificial Intelligence complete the range.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>At doctoral level, the portfolio includes the Doctor of Business Administration (DBA), the Doctor of Laws (PhD) with specializations in areas such as Administrative Law and Administrative Procedure, Criminal Law, Constitutional Law, Civil and Family Law, International Law and Relations, Criminology, State Law and Theory as well as the Doctor of Economics (PhD) in subjects such as Organization and Management, Economics and Management in Industry, Political Economy, World Economy and International Economic Relations and Accounting, Control and Analysis of Business.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Students can also choose the Doctor of Engineering &amp; IT (PhD) with a focus on Information Sciences and Green Energetics as well as the Doctor of Health Sciences (PhD).</p>', 'en'),
(30, 131, 'Welche Arten von Studiengängen bietet die ONSITES Graduate School an?', '<p>In enger Zusammenarbeit mit unseren international anerkannten Partnerhochschulen stellt die ONSITES Graduate School eine breite Palette an Studienprogrammen auf <a href=\"https://graduate.me/de/bachelor\">Bachelor</a>-, <a href=\"https://graduate.me/de/master\">Master</a>-, <a href=\"https://graduate.me/de/mba\">MBA</a>- und <a href=\"https://graduate.me/de/doktorat\">Doktorats</a>-Niveau zur Verf&uuml;gung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Auf Bachelor-Ebene k&ouml;nnen Studierende zwischen dem Bachelor of Business Administration (Fast Track) und dem Bachelor Professional in Business (Wirtschaftsfachwirt) w&auml;hlen.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Auf Master-Niveau stehen Programme wie der Master of IT Management, Master of Cybersecurity, Master of Green Energetics, Master of Laws sowie der Master of Health Sciences und der Master Professional in Business (Betriebswirt) zur Verf&uuml;gung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Erg&auml;nzt wird das Angebot durch den Executive Master of Business Administration (MBA) und den MBA Professional in Artificial Intelligence.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Auf Doktoratsniveau beinhaltet das Portfolio den Doctor of Business Administration (DBA), den Doctor of Laws (PhD) mit Spezialisierungen in Bereichen wie Administrative Law and Administrative Procedure, Criminal Law, Constitutional Law, Civil and Family Law, International Law and Relations, Criminology, State Law and Theory sowie den Doctor of Economics (PhD) in Fachrichtungen wie Organization and Management, Economics and Management in Industry, Political Economy, World Economy and International Economic Relations und Accounting, Control and Analysis of Business.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Zus&auml;tzlich k&ouml;nnen Studierende den Doctor of Engineering &amp; IT (PhD) mit den Schwerpunkten Information Sciences und Green Energetics sowie den Doctor of Health Sciences (PhD) w&auml;hlen.</p>', 'de'),
(31, 132, 'How are the study programs structured at ONSITES Graduate School?', '<p>Our study programs follow a modular structure. They are designed in such a way that the knowledge imparted builds on each other.</p>', 'en'),
(32, 132, 'Wie ist das Studium an der ONSITES Graduate School aufgebaut?', '<p>Unsere Studieng&auml;nge unterliegen einem modularen Aufbau. Sie sind so gestaltet, dass das vermittelte Wissen aufeinander aufbaut.&nbsp;</p>', 'de'),
(33, 133, 'Are there any mandatory in-person events?', '<p>At present, the study regulations do not provide for any attendance events. All events take place online &ndash; flexibly, independent of time and location.</p>', 'en'),
(34, 133, 'Gibt es obligatorische Präsenzveranstaltungen?', '<p>Die Studienordnung sieht zum gegenw&auml;rtigen Zeitpunkt keine Pr&auml;senzveransaltungen vor, Alle Veranstaltungen finden online statt &ndash; flexibel, zeit- und ortsunabh&auml;ngig.</p>', 'de'),
(35, 134, 'What are the types of examinations?', '<p>Each module includes an examination, which can be in the form of an online test, a term paper, a project paper, or a case study. Each program concludes with the preparation of a thesis.</p>', 'en'),
(36, 134, 'Welche Prüfungsformen gibt es?', '<p>Zu jedem Modul muss eine Pr&uuml;fung durchgef&uuml;hrt werden, welche entweder online stattfindet oder in Form einer Hausarbeit, Projektarbeit bzw. Fallstudie absolviert wird. Jedes Programm schlie&szlig;t mit der Anfertigung einer Abschlussarbeit ab.</p>', 'de'),
(37, 135, 'How are examinations conducted?', '<p>Currently all examinations are conducted online.</p>', 'en'),
(38, 135, 'Wie finden die Prüfungen statt?', '<p>Derzeit werden s&auml;mtliche Pr&uuml;fungen online durchgef&uuml;hrt.</p>', 'de'),
(39, 136, 'Can I retake exams?', '<p>Our study materials are designed to ensure proper exam preparation. However, if an examination is failed, there is an opportunity to retake it up to two&nbsp;times.</p>', 'en'),
(40, 136, 'Kann ich Prüfungen wiederholen?', '<p>Unser Studienmaterial ist so konzipiert, dass eine angemessene Pr&uuml;fungsvorbereitung gew&auml;hrleistet ist. Wenn eine Pr&uuml;fung nicht bestanden wird, besteht die M&ouml;glichkeit, sie bis zu zweimal zu wiederholen.</p>', 'de'),
(41, 137, 'Can I transfer previous study achievements?', '<p>Absolutely. We recognize examinations taken at other educational institutions, and prior to enrollment, we evaluate the relevant documents to determine which previous study achievements can be <a href=\"https://graduate.me/en/recognition-of-prior-learning\">recognized</a>.</p>', 'en'),
(42, 137, 'Kann ich frühere Studienleistungen anrechnen lassen?', '<p>Ja. Wir erkennen Pr&uuml;fungen an, die an anderen Bildungseinrichtungen abgelegt wurden. Vor der Immatrikulation pr&uuml;fen wir die entsprechenden Dokumente, um festzustellen, welche fr&uuml;heren Studienleistungen <a href=\"https://graduate.me/de/studium-verkuerzen\">anerkannt </a>werden k&ouml;nnen.</p>', 'de'),
(43, 138, 'How can I obtain more information about my desired study program?', '<p>For further information about your desired study program, you can reach out to us through the contact form, via email, or by telephone.</p>', 'en'),
(44, 138, 'Wie kann ich mehr Informationen über mein Wunschstudium erhalten?', '<p>N&auml;here Informationen zu Deinem Wunschstudium kannst Du &uuml;ber das Kontaktformular, per E-Mail oder telefonisch erhalten.</p>', 'de'),
(45, 139, 'What documents do I need to submit for enrollment?', '<p>To enroll in a study program, you need to submit the enrollment form along with the following documents: identification card or passport, curriculum vitae, graduation diploma, and any previously obtained diplomas and certificates. All documents from students within the European Union must be notarized. Students outside the European Union require both notarization and an apostille.</p>', 'en'),
(46, 139, 'Welche Dokumente muss ich bei der Immatrikulation einreichen?', '<p>Um sich f&uuml;r einen Studiengang einzuschreiben, musst Du den Immatrikulationsantrag zusammen mit den folgenden Dokumenten einreichen: Personalausweis oder Reisepass, Lebenslauf und alle Abschlusszeugnisse. S&auml;mtliche Dokumente von Studierenden innerhalb der Europ&auml;ischen Union m&uuml;ssen notariell beglaubigt werden. Studierende au&szlig;erhalb der Europ&auml;ischen Union ben&ouml;tigen sowohl eine notarielle Beglaubigung als auch eine Apostille.</p>', 'de'),
(47, 140, 'When can I start my studies at ONSITES Graduate School?', '<p>You can begin your studies at any time.</p>', 'en'),
(48, 140, 'Wann kann ich das Studium aufnehmen?', '<p>Der Einstieg ins Studium ist jederzeit m&ouml;glich.</p>', 'de'),
(49, 141, 'What is the duration of study?', '<p>The standard period of study for <a href=\"https://graduate.me/en/bachelor-degree\">Bachelor</a> programs at ONSITES Graduate School is one year, while <a href=\"https://graduate.me/en/master-degree\">Master</a> and <a href=\"https://graduate.me/en/mba\">MBA</a> programs usually take one to two years. For <a href=\"https://graduate.me/en/doctorate\">Doctorate</a> programs, the duration is one to three years.</p>', 'en'),
(50, 141, 'Wie lange ist die Studiendauer?', '<p>Die Regelstudienzeit f&uuml;r das&nbsp;<a href=\"https://graduate.me/de/bachelor\">Bachelor</a>-Studium&nbsp;an der ONSITES Graduate School betr&auml;gt ein Jahr, w&auml;hrend <a href=\"https://graduate.me/de/master\">Master</a>-Studieng&auml;nge und <a href=\"https://graduate.me/de/mba\">MBA</a>-Studieng&auml;nge in der Regel ein bis zwei Jahre dauern. F&uuml;r <a href=\"https://graduate.me/de/doktorat\">Doktorate</a> betr&auml;gt die Dauer ein bis drei Jahre.</p>', 'de'),
(51, 142, 'Can I accelerate my studies?', '<p>Certainly! At ONSITES Graduate School, we recognize previous study achievements from other educational institutions, which shortens the standard period of study. Additionally, if you have completed vocational training and professional experience, you have the option to enroll in the <a href=\"https://graduate.me/en/bachelor-degree/bba-fast-track\">Fast-Track</a> Bachelor program.</p>', 'en'),
(52, 142, 'Kann ich mein Studium beschleunigen?', '<p>Ja! An der ONSITES Graduate School erkennen wir fr&uuml;here Studienleistungen aus anderen Bildungseinrichtungen an, wodurch sich die Regelstudienzeit verk&uuml;rzt. Wenn Du eine abgeschlossene Berufsausbildung und Berufserfahrung hast, kannst Du Dich au&szlig;erdem f&uuml;r das <a href=\"https://graduate.me/de/bachelor/bba\">Fast-Track</a>-Bachelor-Studium einschreiben.</p>', 'de'),
(53, 143, 'Do I need to undergo selection or admission test procedures for admission to study at ONSITES Graduate School?', '<p>Certain study programs at ONSITES Graduate School, such as law, may require applicants to undergo selection or admission test procedures. If such procedures are necessary, we offer targeted and intensive preparation to assist you through the process.</p>', 'en'),
(54, 143, 'Muss ich für die Zulassung zum Studium an der ONSITES Graduate School ein Auswahl- oder Zulassungstestverfahren durchlaufen?', '<p>F&uuml;r bestimmte Studieng&auml;nge an der ONSITES Graduate School, wie z. B. im Bereich Jura, ist m&ouml;glicherweise ein Auswahl- oder Zulassungstestverfahren erforderlich. Wenn solche Verfahren notwendig sind, bieten wir eine gezielte und intensive Vorbereitung an, um Dich durch das Verfahren zu begleiten.</p>', 'de'),
(55, 144, 'Who can assist me with administrative questions?', '<p>Our dedicated support team at ONSITES Graduate School is here to assist you with any administrative questions you may have. You can reach out to them via email or telephone.</p>', 'en'),
(56, 144, 'Wer beantwortet meine administrativen Fragen?', '<p>Bei administrativen Fragen w&auml;hrend des Studiums ist der Support der ONSITES Graduate School behilflich, welcher per E-Mail oder fernm&uuml;ndlich erreichbar ist.</p>', 'de'),
(57, 145, 'Who provides academic support during my studies at ONSITES Graduate School?', '<p>Academic support during your studies at ONSITES Graduate School is generally provided by your respective supervisor, who will guide and assist you with academic matters.</p>', 'en'),
(58, 145, 'Wer bietet akademische Unterstützung während meines Studiums an der ONSITES Graduate School?', '<p>Die akademische Betreuung w&auml;hrend des Studiums an der ONSITES Graduate School erfolgt in der Regel durch den zust&auml;ndigen Betreuer, der Dich in akademischen Fragen anleitet und unterst&uuml;tzt.</p>', 'de'),
(59, 146, 'How many hours of study should I schedule per week?', '<p>As each student has a unique learning style, it varies. However, as a general guideline, it is recommended to schedule between eight to twelve hours of study per week to ensure progress and success in your studies.</p>', 'en'),
(60, 146, 'Wie viele Stunden sollte ich pro Woche für das Studium einplanen?', '<p>Da jeder Student einen individuellen Lernstil hat, ist dies unterschiedlich. Als allgemeine Richtlinie wird jedoch empfohlen, zwischen acht und zw&ouml;lf Stunden pro Woche einzuplanen, um Fortschritte und Erfolg im Studium zu gew&auml;hrleisten.</p>', 'de'),
(61, 147, 'Can I balance my studies with my professional activities?', '<p>Yes, all degree programs at ONSITES Graduate School are part-time. You study without having to interrupt your full-time job. Thanks to the free time management, you determine your own pace and can optimally adapt your studies to your job.</p>', 'en'),
(62, 147, 'Kann ich das Studium mit meinem Beruf vereinbaren?', '<p>Ja. S&auml;mtliche Studieng&auml;nge an der ONSITES Graduate School sind berufsbegleitend. Du studierst, ohne Deine Vollzeitt&auml;tigkeit unterbrechen zu m&uuml;ssen. Dank der freien Zeiteinteilung bestimmst Du Dein eigenes Tempo und kannst das Studium optimal Deinem Beruf anpassen.</p>', 'de'),
(63, 148, 'Can I gain practical experience in foreign countries while studying at ONSITES Graduate School?', '<p>Yes, ONSITES Graduate School provides opportunities for students to gain practical experience through internship placements with partner companies. However, please note that internship placements are limited and highly competitive.</p>', 'en'),
(64, 148, 'Kann ich während des Studiums an der ONSITES Graduate School praktische Erfahrungen in anderen Ländern sammeln?', '<p>Ja, die ONSITES Graduate School bietet Studierenden die M&ouml;glichkeit, durch Praktika bei Partnerunternehmen praktische Erfahrungen zu sammeln. Bitte beachte jedoch, dass die Zahl der Praktikumspl&auml;tze begrenzt und die Nachfrage sehr gro&szlig; ist.</p>', 'de'),
(65, 149, 'What are the tuition fees at ONSITES Graduate School?', '<p>Tuition fees at ONSITES Graduate School vary based on factors such as the partnership university, study program, and study duration. Prior to enrollment, the amount of tuition fees will be fully transparent and agreed upon with binding effect.</p>', 'en'),
(66, 149, 'Wie hoch sind die Studiengebühren an der ONSITES Graduate School?', '<p>Die Studiengeb&uuml;hren an der ONSITES Graduate School variieren je nach Partnerhochschule, Studienprogramm und&nbsp;Studiendauer. Vor der Immatrikulation wird die H&ouml;he der Studiengeb&uuml;hren vollst&auml;ndig transparent gemacht und verbindlich vereinbart.</p>', 'de'),
(67, 150, 'Are there any additional costs besides tuition fees?', '<p>There may be additional costs for the recognition of previous achievements. These costs, if applicable, will be fully transparent and agreed upon with binding effect. Students are responsible for expenses such as travel costs, internet and telephone charges, as well as the acquisition of additional or recommended literature.</p>', 'en'),
(68, 150, 'Gibt es neben den Studiengebühren noch weitere Kosten?', '<p>Es k&ouml;nnen zus&auml;tzliche Kosten f&uuml;r die Anerkennung von Vorleistungen anfallen. Diese Kosten werden, falls zutreffend, vollst&auml;ndig transparent gemacht und verbindlich vereinbart. Ausgaben wie Reisekosten, Internet- und Telefongeb&uuml;hren sowie die Anschaffung zus&auml;tzlicher oder empfohlener Literatur gehen zu Lasten der Studierenden.</p>', 'de'),
(69, 151, 'In which languages can I study?', '<p>Our study programs are conducted in English, German, and Russian.</p>', 'en'),
(70, 151, 'In welchen Sprachen kann ich studieren?', '<p>Unsere Studienprogramme werden auf Englisch und&nbsp;Deutsch durchgef&uuml;hrt.</p>', 'de'),
(71, 152, 'Are studies completed abroad equivalent to study programs in my home country?', '<p>Yes, studies completed abroad hold equivalent recognition when the university in the home country is state-recognized and the study programs are accredited. At ONSITES Graduate School, the accreditation of the study programs is guaranteed by its cooperation with <a href=\"https://graduate.me/en/accreditation-partners\">accredited</a>, state-recognized cooperation partners.</p>', 'en'),
(72, 152, 'Sind im Ausland absolvierte Studiengänge gleichwertig mit Studiengängen in meinem Heimatland?', '<p>Ja, ein im Ausland absolviertes Studium ist gleichwertig, wenn die Hochschule im Heimatland staatlich anerkannt ist und die Studieng&auml;nge akkreditiert sind. An der ONSITES Graduate School ist die Akkreditierung der Studieng&auml;nge ist durch ihre Zusammenarbeit mit <a href=\"https://graduate.me/de/akkreditierung-und-partner\">akkreditierten</a>, staatlich anerkannten Kooperationspartnern gew&auml;hrleistet.</p>', 'de'),
(73, 153, 'What are the career prospects after completing a study program at ONSITES Graduate School?', '<p>ONSITES Graduate School offers future-proof study programs. Our range of studies largely covers areas in which there is a considerable skills shortage, including Digitalization &amp; IT, Healthcare, and Business. A successful degree thus opens up excellent career opportunities.</p>', 'en'),
(74, 153, 'Wie sind die Berufsaussichten nach Abschluss eines Studiums an der ONSITES Graduate School?', '<p>Die ONSITES Graduate School bietet Studieng&auml;nge mit Zukunft. Unser Studienangebot erstreckt sich zum gro&szlig;en Teil &uuml;ber Bereiche, in denen ein betr&auml;chtlicher Fachkr&auml;ftemangel besteht, darunter Digitalisierung &amp; IT, Healthcare und Wirtschaft. Ein erfolgreicher Abschluss er&ouml;ffnet damit hervorragende Karrierechancen.</p>', 'de'),
(95, 164, 'Why is it useful to attend a conference?', '<p>Attending a conference provides numerous benefits for academics. Conferences offer an opportunity to present and discuss your latest research findings with peers in your field. They also foster collaboration, networking, and the exchange of knowledge, allowing you to stay updated with the latest developments in your area of study.</p>', 'en'),
(96, 164, 'Warum ist die Teilnahme an einer Konferenz sinnvoll?', '<p>Die Teilnahme an einer Konferenz bietet zahlreiche Vorteile f&uuml;r Akademiker. Auf Konferenzen kannst Du Deine neuesten Forschungsergebnisse zu pr&auml;sentieren und mit Kollegen aus Deinem Fachgebiet diskutieren. Zudem f&ouml;rdern sie die Zusammenarbeit, die Vernetzung und den Wissensaustausch und erm&ouml;glichen es Dir, Dich &uuml;ber die neuesten Entwicklungen in Deinem Fachgebiet zu informieren.</p>', 'de'),
(97, 165, 'How can I register to participate in a conference?', '<p>To register for a conference, please visit the <a href=\"https://graduate.me/en/event-calendar\">event calendar</a> on ONSITES Graduate School&#39;s platform. Choose the conference you are interested in and then complete the registration form with the required information.</p>', 'en'),
(98, 165, 'Wie erfolgt die Anmeldung zu einer Konferenz?', '<p>Um sich f&uuml;r eine Konferenz anzumelden, besuche bitte unseren <a href=\"https://graduate.me/de/eventkalender\">Eventkalender</a>. W&auml;hlen die Konferenz aus, f&uuml;r die Du Dich interessierst, und f&uuml;lle dann das Anmeldeformular mit den erforderlichen Angaben aus.</p>', 'de'),
(99, 166, 'In which languages are conferences conducted at ONSITES Graduate School?', '<p>Conferences are generally conducted in English. However, depending on the location of the specific event, conferences may also be conducted in other languages.</p>', 'en'),
(100, 166, 'In welchen Sprachen erfolgen Konferenzen an der ONSITES Graduate School?', '<p>Unsere Konferenzen erfolgen grunds&auml;tzlich in englischer Sprache. Je nach Veranstaltungsort k&ouml;nnen die Konferenzen jedoch auch in anderen Sprachen abgehalten werden.</p>', 'de'),
(101, 167, 'Are conferences conducted online as well?', '<p>Yes, conferences can be conducted both as physical attendance events and online. This offers participants maximum flexibility in terms of time and location.</p>', 'en'),
(102, 167, 'Werden Konferenzen auch online durchgeführt?', '<p>Ja, Konferenzen k&ouml;nnen sowohl als Pr&auml;senzveranstaltungen als auch online durchgef&uuml;hrt werden. Dies bietet den Teilnehmern eine h&ouml;chstm&ouml;gliche &ouml;rtliche und zeitliche Flexibilit&auml;t.</p>', 'de'),
(103, 168, 'I intend to present my research results at a conference. What is the process?', '<p>After registering for a conference, you should contact the support team at ONSITES Graduate School via the contact form or phone. They will assist in registering you as an active presenter for your chosen conference. Prepare a research paper, abstract, and a poster summarizing your research in a concise format.</p>', 'en'),
(104, 168, 'Wie kann ich meine Forschungsergebnisse auf einer Konferenz präsentieren?', '<p>Nachdem Du Dich f&uuml;r eine Konferenz angemeldet hast, solltest Du das Support-Team der ONSITES Graduate School &uuml;ber das Kontaktformular oder telefonisch kontaktieren. Dort wird man Dir helfen, Dich als aktiver Referent f&uuml;r die von Dir gew&auml;hlte Konferenz zu registrieren. Bereite einen Forschungsartikel, einen Abstract und ein Poster vor, um Deine Forschung in einem pr&auml;gnanten Format zusammenzufassen.</p>', 'de'),
(105, 169, 'What type of research results can I present at a conference?', '<p>Conferences welcome the presentation of research results from various sources, including doctoral theses, habilitation treatises, other research works, or peer-reviewed papers. In some cases, well-structured and methodologically sound Bachelor&#39;s or Master&#39;s theses with significant empirical density may also be considered for presentation at scientific conferences.</p>', 'en'),
(106, 169, 'Welche Art von Forschungsergebnissen kann ich auf einer Konferenz vorstellen?', '<p>Auf Konferenzen k&ouml;nnen Forschungsergebnisse aus verschiedenen Quellen vorgestellt werden, z. B. aus Dissertationen, Habilitationsschriften, sonstigen Forschungsarbeiten oder Peer-Review-Artikeln oder sonstigen Forschungsarbeiten. In einigen F&auml;llen k&ouml;nnen auch gut strukturierte und methodisch fundierte Bachelorarbeiten oder Masterarbeiten mit hoher empirischer Dichte f&uuml;r die Pr&auml;sentation auf wissenschaftlichen Konferenzen in Betracht gezogen werden.</p>', 'de'),
(107, 170, 'How does the presentation process work?', '<p>During the conference, you will deliver a presentation of your scientific results using a PowerPoint presentation. The presentation should typically last no longer than thirty minutes. Creating a poster to accompany your presentation is mandatory as it serves as a visual aid and facilitates discussions during the poster session following the presentation.</p>', 'en'),
(108, 170, 'Wie läuft der Präsentationsprozess ab?', '<p>W&auml;hrend der Konferenz h&auml;ltst Du einen Vortrag &uuml;ber Deine wissenschaftlichen Ergebnisse anhand einer PowerPoint-Pr&auml;sentation. Die Pr&auml;sentation sollte in der Regel nicht l&auml;nger als drei&szlig;ig Minuten dauern. Die Erstellung eines Posters zu Deiner Pr&auml;sentation ist obligatorisch, da es als visuelles Hilfsmittel dient und die Diskussionen w&auml;hrend der Postersitzung im Anschluss an die Pr&auml;sentation erleichtert.</p>', 'de'),
(109, 171, 'How can I publish the research results presented at the conference?', '<p>The research results presented at the conference can be published in a conference proceeding specific to that event. The conference proceeding is a collection of research papers and findings from the conference participants.</p>', 'en'),
(110, 171, 'Wie kann ich die auf der Konferenz vorgetragenen Forschungsergebnisse veröffentlichen?', '<p>Die auf der Konferenz vorgestellten Forschungsergebnisse k&ouml;nnen in einem Konferenzband zu dieser Veranstaltung ver&ouml;ffentlicht werden. Der Band ist eine Sammlung von Forschungsarbeiten und Ergebnissen der Konferenzteilnehmer.</p>', 'de'),
(111, 172, 'Why is attending a workshop useful?', '<p>Our workshops focus on practice-oriented topics and offer participants the opportunity to develop their problem-solving and decision-making skills. By actively participating in small group activities, attendees can contribute their unique perspectives and learn from others. Workshops enhance analytical thinking, communication, and teamwork skills, which are valuable in professional settings.</p>', 'en'),
(112, 172, 'Warum ist die Teilnahme an einem Workshop sinnvoll?', '<p>Unsere Workshops konzentrieren sich auf praxisorientierte Themen und bieten den Teilnehmern die M&ouml;glichkeit, ihre Probleml&ouml;sungs- und Entscheidungsfindungsf&auml;higkeiten zu entwickeln. Durch die aktive Teilnahme in kleinen Gruppen k&ouml;nnen die Teilnehmer ihre eigenen Perspektiven einbringen und von anderen lernen. Die Workshops verbessern das analytische Denken, die Kommunikation und die F&auml;higkeit zur Teamarbeit, die im beruflichen Umfeld von gro&szlig;em Wert sind.</p>', 'de'),
(113, 173, 'How can I register for a workshop?', '<p>To register for a workshop, please go to the <a href=\"https://graduate.me/en/event-calendar\">event calendar</a> on our website. Choose the workshop you are interested in and complete the registration form by providing the necessary information.</p>', 'en'),
(114, 173, 'Wie erfolgt die Anmeldung zu einem Workshop?', '<p>Um sich f&uuml;r einen Workshop anzumelden, rufe bitte den <a href=\"https://graduate.me/de/eventkalender\">Eventkalender</a> auf unserer Website auf. W&auml;hle den gew&uuml;nschten Workshop und f&uuml;lle das Anmeldeformular mit den erforderlichen Angaben aus.</p>', 'de'),
(115, 174, 'In which languages are workshops conducted?', '<p>Currently, workshops are conducted in English and German.</p>', 'en'),
(116, 174, 'In welchen Sprachen werden die Workshops durchgeführt?', '<p>Derzeit werden die Workshops auf Englisch und Deutsch durchgef&uuml;hrt.</p>', 'de'),
(117, 175, 'Are workshops conducted online as well?', '<p>Yes, workshops are conducted both as physical attendance events and online. This provides participants the highest-possible flexibility in terms of location and time.</p>', 'en'),
(118, 175, 'Werden die Workshops auch online durchgeführt?', '<p>Ja, die Workshops werden sowohl als Pr&auml;senzveranstaltungen als auch online durchgef&uuml;hrt. Dies bietet den Teilnehmern maximale &ouml;rtliche und zeitliche Flexibilit&auml;t.</p>', 'de'),
(119, 176, 'What types of workshops are offered?', '<p>The ONSITES Graduate School offers various types of workshops, including problem-solution workshops, conflict-resolution workshops, conception workshops, and decision-making workshops. Each workshop focuses on a specific area and provides practical guidance and skills development.</p>', 'en'),
(120, 176, 'Welche Arten von Workshops werden angeboten?', '<p>Die ONSITES Graduate School bietet verschiedene Arten von Workshops an, darunter Probleml&ouml;sungsworkshops, Konfliktl&ouml;sungsworkshops, Konzeptionsworkshops und Workshops zur Entscheidungsfindung. Jeder Workshop konzentriert sich auf einen bestimmten Bereich und bietet praktische Anleitung und Kompetenzentwicklung.</p>', 'de'),
(121, 177, 'Will the outcomes of the workshop be published?', '<p>No, the outcomes of workshops are typically not published. Workshops are designed to foster collaborative problem-solving and learning within the workshop setting. The emphasis is on the interactive exchange of ideas and practical application, rather than formal publication of workshop outcomes.</p>', 'en'),
(122, 177, 'Werden die Ergebnisse des Workshops veröffentlicht?', '<p>Nein, die Ergebnisse von Workshops werden normalerweise nicht ver&ouml;ffentlicht. Die Workshops sind so konzipiert, dass sie das gemeinsame L&ouml;sen von Problemen und das Lernen im Rahmen des Workshops f&ouml;rdern. Der Schwerpunkt liegt auf dem interaktiven Austausch von Ideen und der praktischen Anwendung und nicht auf der formellen Ver&ouml;ffentlichung von Workshop-Ergebnissen.</p>', 'de'),
(123, 178, 'What are the general admission requirements for doctorates?', '<p>In order to be admitted to a doctorate program, you must have a master&#39;s degree or equivalent.</p>', 'en'),
(124, 178, 'Welche allgemeinen Zulassungsvoraussetzungen gelten bei Doktoraten?', '<p>Um zu einem Doktoratsstudium zugelassen zu werden, muss ein Master-Abschluss bzw. ein gleichwertiger Abschluss vorliegen.</p>', 'de'),
(125, 179, 'What options does ONSITES Graduate School provide for publishing scientific research outcomes?', '<p>ONSITES Graduate School offers various avenues for publishing scientific research outcomes, including conference proceedings, and monographs.</p>', 'en'),
(126, 179, 'Welche Optionen bietet die ONSITES Graduate School zur Veröffentlichung wissenschaftlicher Ergebnisse?', '<p>Die ONSITES Graduate School bietet verschiedene M&ouml;glichkeiten zur Ver&ouml;ffentlichung wissenschaftlicher Forschungsergebnisse, darunter Konferenzb&auml;nde und Monografien.</p>', 'de'),
(127, 180, 'How can I publish a scientific paper in a conference proceeding?', '<p>ONSITES Graduate School regularly announces conferences on its website&#39;s <a href=\"https://graduate.me/en/event-calendar\">event calendar</a>, where you&nbsp;can register. If you present your research findings at one of these conferences, you will have the opportunity to publish your paper in the corresponding conference proceeding.</p>', 'en'),
(128, 180, 'Wie kann ich einen wissenschaftlichen Beitrag in einem Konferenzband veröffentlichen?', '<p>Die ONSITES Graduate School k&uuml;ndigt regelm&auml;&szlig;ig Konferenzen im <a href=\"https://graduate.me/de/eventkalender\">Eventkalender</a> ihrer Website an, zu denen Du Dich anmelden kannst. Wenn Du Deine Forschungsergebnisse auf einer dieser Konferenzen pr&auml;sentierst, hast Du die M&ouml;glichkeit, Denen Beitrag in dem entsprechenden Konferenzband zu ver&ouml;ffentlichen.</p>', 'de'),
(129, 181, 'In which language can I publish my paper?', '<p>All academic papers can be published in English only.</p>', 'en'),
(130, 181, 'In welcher Sprache kann ich meine Fachartikel veröffentlichen?', '<p>S&auml;mtliche wissenschaftlichen Artikel k&ouml;nnen nur auf Englisch ver&ouml;ffentlicht werden.</p>', 'de'),
(131, 182, 'How can I publish my doctoral thesis or monograph?', '<p>For assistance with publishing your doctoral thesis or monograph, you can contact the support team at ONSITES Graduate School through the contact form or by phone.</p>', 'en'),
(132, 182, 'Wie kann ich meine Dissertation oder Monographie veröffentlichen?', '<p>Wenn Du Unterst&uuml;tzung bei der Ver&ouml;ffentlichung Deiner Dissertation oder Monografie ben&ouml;tigst, kannst Du Dich &uuml;ber das Kontaktformular oder telefonisch an das Support-Team der ONSITES Graduate School wenden.</p>', 'de'),
(133, 183, 'Are monographs also subjected to a peer-review process?', '<p>While a peer-review process is not mandatory for monographs, it is recommended as it significantly enhances the value and credibility of the work.</p>', 'en'),
(134, 183, 'Werden auch Monografien einem Peer-Review-Verfahren unterzogen?', '<p>Ein Peer-Review-Verfahren ist f&uuml;r Monografien zwar nicht zwingend vorgeschrieben, wird aber empfohlen, da es den Wert und die Glaubw&uuml;rdigkeit der Arbeit erheblich steigert.</p>', 'de'),
(135, 184, 'What services does ONSITES Graduate School offer for book publications?', '<p>When it comes to book publications, ONSITES Graduate School provides comprehensive services, including obtaining an ISBN number, registration in relevant book directories, such as the German &quot;Verzeichnis Lieferbarer B&uuml;cher&quot; (VLB), and inclusion in the national library. Furthermore, marketing services are available to ensure the wide distribution of the book. ONSITES Graduate School can also provide guidance on royalty disbursement from secondary rights of academic texts.</p>', 'en'),
(136, 184, 'Welche Dienstleistungen bietet die ONSITES Graduate School für Buchveröffentlichungen an?', '<p>Bei Buchver&ouml;ffentlichungen bietet die ONSITES Graduate School umfassende Dienstleistungen an, darunter die Beschaffung einer ISBN-Nummer, die Eintragung in einschl&auml;gige Buchverzeichnisse wie das Verzeichnis Lieferbarer B&uuml;cher (VLB) und die Aufnahme in die Nationalbibliothek. Dar&uuml;ber hinaus stehen Marketingdienste zur Verf&uuml;gung, um die weite Verbreitung des Buches zu gew&auml;hrleisten. Die ONSITES Graduate School kann auch bei der Auszahlung von Tantiemen aus Zweitverwertungsrechten an wissenschaftlichen Texten behilflich sein.</p>', 'de'),
(137, 185, 'Why should I choose ONSITES Graduate School for publishing my scientific paper or academic text?', '<p>ONSITES Graduate School offers not only the complete package of typical <a href=\"https://graduate.me/en/book-publishing\">publishing services</a>, but also comprehensive marketing support. We support you in establishing a strong presence in the professional and academic spheres as well as making a name for yourself in your respective field.</p>', 'en'),
(138, 185, 'Warum sollte ich ONSITES Graduate School für die Veröffentlichung meiner wissenschaftlichen Arbeit oder meines akademischen Textes wählen?', '<p>Die ONSITES Graduate School bietet nicht nur das komplette Paket typischer <a href=\"https://graduate.me/de/buch-veroeffentlichen\">Publikationsdienstleistungen</a>, sondern leistet auch umfassende Marketingunterst&uuml;tzung. Wir unterst&uuml;tzen Dich dabei, eine starke Pr&auml;senz in der professionellen und akademischen Welt aufzubauen und Dir einen Namen in Deinem Fachgebiet zu machen.</p>', 'de'),
(139, 186, 'What types of coaching does the ONSITES Graduate School offer?', '<p>ONSITES Graduate School offers individual coachings and group coachings.</p>', 'en'),
(140, 186, 'Welche Arten von Coaching bietet die ONSITES Graduate School an?', '<p>Die ONSITES Graduate School bietet Einzelcoachings und Gruppencoachings an.</p>', 'de'),
(141, 187, 'What languages is a coaching conducted in?', '<p>Coachings are conducted in English and German.</p>', 'en'),
(142, 187, 'In welchen Sprachen wird ein Coaching durchgeführt?', '<p>Die Coachings werden auf Englisch und Deutsch durchgef&uuml;hrt.</p>', 'de'),
(143, 188, 'How can I register for a coaching?', '<p>Requests for coaching sessions can be made to the ONSITES Graduate School&rsquo;s support team via the contact form or by telephone.</p>', 'en'),
(144, 188, 'Wie kann ich mich für ein Coaching anmelden?', '<p>Anfragen f&uuml;r Coaching-Sitzungen k&ouml;nnen &uuml;ber das Kontaktformular oder per Telefon an das Support-Team der ONSITES Graduate School gerichtet werden.</p>', 'de'),
(145, 189, 'Why should I pursue a doctorate?', '<p>A doctorate fosters intellectual growth and strengthens one&#39;s character, and enhances prospects for professional positions in various industries. In certain areas, a doctoral degree opens up better opportunities for achieving management positions.</p>', 'en'),
(146, 189, 'Warum sollte ich ein Doktorat anstreben?', '<p>Ein Doktorat f&ouml;rdert die intellektuelle Entwicklung, festigt den eigenen Charakter und verbessert die Aussichten auf berufliche Positionen in verschiedenen Branchen. In gewissen Bereichen er&ouml;ffnet ein Doktor-Titel bessere M&ouml;glichkeiten, F&uuml;hrungspositionen zu erreichen.</p>', 'de'),
(147, 190, 'Why should I pursue my doctorate at ONSITES Graduate School?', '<p>At ONSITES Graduate School, we provide excellent research and study conditions, fostering scientific and technical exchange among doctoral students. Our collaboration with numerous companies enables the integration of university and industrial research in an optimal manner. Doctoral students have the opportunity to pursue their doctorate at one of ONSITES Graduate School&#39;s <a href=\"https://graduate.me/en/accreditation-partners\">partnership universities</a>, offering programs in English and&nbsp;German.</p>', 'en'),
(148, 190, 'Warum sollte die ONSITES Graduate School für ein Doktorat auswählen?', '<p>Die ONSITES Graduate School bietet hervorragende Forschungs- und Studienbedingungen und f&ouml;rdert den wissenschaftlichen und fachlichen Austausch unter den Doktoranden. Unsere Zusammenarbeit mit zahlreichen Unternehmen erm&ouml;glicht die optimale Integration von universit&auml;rer und industrieller Forschung. Die Doktoranden haben die M&ouml;glichkeit, ihr Doktorat an einer der <a href=\"https://graduate.me/de/akkreditierung-und-partner\">Partnerhochschulen</a> der ONSITES Graduate School zu absolvieren, die Programme auf Englisch und&nbsp;Deutsch anbieten.</p>', 'de'),
(149, 191, 'What other benefits does a doctorate course offer?', '<p>During doctorates, we offer comprehensive support for attending scientific <a href=\"https://graduate.me/en/conferences-workshops\">conferences and&nbsp;workshops</a>, as well as&nbsp;publishing peer-reviewed papers in scientific journals. <a href=\"https://graduate.me/en/coaching-services\">Coaching</a> measures for career development are also available to doctoral students.</p>', 'en'),
(150, 191, 'Welche weiteren Vorteile bietet ein Doktoratsstudium?', '<p>W&auml;hrend des&nbsp;Doktorats&nbsp;bieten wir umfassende Unterst&uuml;tzung bei der Teilnahme an wissenschaftlichen <a href=\"https://graduate.me/de/konferenzen-und-workshops\">Konferenzen und Workshops</a> sowie bei der Ver&ouml;ffentlichung von begutachteten Arbeiten in wissenschaftlichen Zeitschriften. Auch <a href=\"https://graduate.me/de/coaching\">Coaching</a>-Ma&szlig;nahmen zur Karriereentwicklung stehen den Promovierenden zur Verf&uuml;gung.</p>', 'de'),
(151, 192, 'Are the doctorates provided by ONSITES Graduate School state-recognized/accredited?', '<p>All doctoral programs offered by ONSITES Graduate School in collaboration with our partner universities are recognized by relevant authorities and accredited.</p>', 'en'),
(152, 192, 'Sind die von der ONSITES Graduate School angebotenen Doktorate staatlich anerkannt/akkreditiert?', '<p>Alle von der ONSITES Graduate School in Zusammenarbeit mit unseren Partneruniversit&auml;ten angebotenen Doktoratsprogramme sind von den zust&auml;ndigen Beh&ouml;rden anerkannt und akkreditiert.</p>', 'de'),
(153, 193, 'In which departments can I pursue my doctorate at ONSITES Graduate School?', '<p>At ONSITES Graduate School, you currently have the opportunity to pursue a doctorate in the following departments:</p>\r\n\r\n<ul>\r\n	<li>Business Administration &amp; Economics</li>\r\n	<li>General Psychology</li>\r\n	<li>Law</li>\r\n	<li>Engineering &amp; IT</li>\r\n</ul>', 'en'),
(154, 193, 'In welchen Fachbereichen kann ich an der ONSITES Graduate School ein Doktorat absolvieren?', '<p>An der ONSITES Graduate School kannst Du derzeit in den folgenden Fachbereichen ein Doktorat absolvieren:</p>\r\n\r\n<ul>\r\n	<li>Wirtschaftswissenschaften</li>\r\n	<li>Allgemeine Psychologie</li>\r\n	<li>Rechtswissenschaften</li>\r\n	<li>Ingenieurwissenschaften &amp; IT</li>\r\n</ul>', 'de'),
(155, 194, 'What types of doctorates does ONSITES Graduate School offer?', '<p>ONSITES Graduate School offers a wide range of doctoral programs in various disciplines. These include the Doctor of Business Administration (DBA), the Doctor of Laws (PhD) with specializations in areas such as Administrative Law and Administrative Procedure, Criminal Law, Constitutional Law, Civil and Family Law, International Law and Relations, Criminology and State Law and Theory. In the field of economics, the Doctor of Economics (PhD) is offered - including specializations in Organization and Management, Economics and Management in Industry, Political Economy, World Economy and International Economic Relations as well as Accounting, Control and Analysis of Business. In addition, programs such as the Doctor of Engineering &amp; IT (PhD) with specializations in Information Sciences and Green Energetics as well as the Doctor of Health Sciences (PhD) are available.</p>', 'en'),
(156, 194, 'Welche Arten von Doktoraten bietet die ONSITES Graduate School an?', '<p>Die ONSITES Graduate School bietet eine vielf&auml;ltige Auswahl an Doktoratsprogrammen in verschiedenen Fachrichtungen an. Dazu z&auml;hlen der Doctor of Business Administration (DBA), der Doctor of Laws (PhD) mit Spezialisierungen in Bereichen wie Administrative Law and Administrative Procedure, Criminal Law, Constitutional Law, Civil and Family Law, International Law and Relations, Criminology sowie State Law and Theory. Im wirtschaftswissenschaftlichen Bereich wird der Doctor of Economics (PhD) angeboten &ndash; unter anderem mit Schwerpunkten in Organization and Management, Economics and Management in Industry, Political Economy, World Economy and International Economic Relations sowie Accounting, Control and Analysis of Business. Dar&uuml;ber hinaus stehen Programme wie der Doctor of Engineering &amp; IT (PhD) mit Spezialisierungen in Information Sciences und Green Energetics sowie der Doctor of Health Sciences (PhD) zur Verf&uuml;gung.</p>', 'de');
INSERT INTO `faq_translations` (`id`, `faq_id`, `question`, `answer`, `locale`) VALUES
(157, 195, 'How is a doctorate program typically structured, and what are the requirements?', '<p>A doctorate program at ONSITES Graduate School follows a compact and modular structure, allowing for efficient progress. Its flexible design accommodates the integration of studies into your professional life. In addition to writing a thesis, requirements may include attending online seminars, <a href=\"https://graduate.me/en/conferences-workshops\">conferences and workshops</a>, solving case studies, and writing and publishing scientific papers.</p>', 'en'),
(158, 195, 'Wie ist ein Doktorat typischerweise aufgebaut und welche Anforderungen gibt es?', '<p>Ein Doktoratsstudium an der ONSITES Graduate School ist kompakt und modular aufgebaut und erm&ouml;glicht ein effizientes Vorankommen. Der flexible Aufbau erm&ouml;glicht es, das Studium in das Berufsleben zu integrieren. Neben dem Verfassen einer Dissertation kann die Teilnahme an Online-Seminaren, <a href=\"https://graduate.me/de/konferenzen-und-workshops\">Konferenzen und Workshops</a>, das L&ouml;sen von Fallstudien sowie das Verfassen und Ver&ouml;ffentlichen wissenschaftlicher Arbeiten zu den Anforderungen geh&ouml;ren.</p>', 'de'),
(159, 196, 'Am I free to choose my own thesis topic?', '<p>Absolutely! You have the freedom to choose your own thesis topic, provided it meets the requirements of the doctoral program.</p>', 'en'),
(160, 196, 'Kann ich das Thema meiner Dissertation frei wählen?', '<p>Auf jeden Fall! Du kannst das Thema Deiner Dissertation frei w&auml;hlen, sofern es den Anforderungen des Doktoratsstudiums entspricht.</p>', 'de'),
(161, 197, 'Are there any mandatory in-person events?', '<p>No. Currently, all events are conducted online.</p>', 'en'),
(162, 197, 'Gibt es obligatorische Präsenzveranstaltungen?', '<p>Nein. Zum gegenw&auml;rtigen Zeitpunkt werden s&auml;mtliche Veranstaltungen online durchgef&uuml;hrt.</p>', 'de'),
(163, 198, 'What examinations must be taken during the doctoral program?', '<p>Doctorate programs typically involve module examinations, including the doctoral dissertation, and conclude with a disputation.</p>', 'en'),
(164, 198, 'Welche Prüfungen müssen während des Doktoratsstudiums abgelegt werden?', '<p>Das Doktoratsstudium umfasst in der Regel Modulpr&uuml;fungen, darunter auch die Dissertation, und schlie&szlig;t mit einer Disputation ab.</p>', 'de'),
(165, 199, 'Where and how are the examinations conducted?', '<p>The examinations for the doctorates are usually taken online. In individual cases, however, it may be necessary to hold the defense on site at the respective partner university.</p>', 'en'),
(166, 199, 'Wo und wie werden die Prüfungen durchgeführt?', '<p>Die Pr&uuml;fungen im Rahmen der Doktorate werden in der Regel online abgelegt. In Einzelf&auml;llen kann jedoch die Durchf&uuml;hrung der Disputation&nbsp;vor Ort an der jeweiligen Partnerhochschule erforderlich sein.</p>', 'de'),
(167, 200, 'What happens if I fail an examination?', '<p>Our study materials are carefully designed to ensure adequate exam preparation. However, in the event of failure, you have the opportunity to retake an examination up to two times.</p>', 'en'),
(168, 200, 'Was passiert, wenn ich eine Prüfung nicht bestehe?', '<p>Unser Studienmaterial ist sorgf&auml;ltig konzipiert, um eine angemessene Pr&uuml;fungsvorbereitung zu gew&auml;hrleisten. Im Falle eines Misserfolgs hast Du jedoch die M&ouml;glichkeit, eine Pr&uuml;fung bis zu zweimal zu wiederholen.</p>', 'de'),
(169, 201, 'Can I transfer previous doctoral achievements from another educational institution to a doctoral program at ONSITES Graduate School?', '<p>Certainly! Previous doctoral achievements from other educational institutions can be recognized for doctoral programs offered at ONSITES Graduate School. Prior to enrollment, we evaluate the relevant documents to determine which previous doctoral achievements can be recognized.</p>', 'en'),
(170, 201, 'Kann ich frühere Doktoratsleistungen von einer anderen Bildungseinrichtung auf ein Doktoratsstudium an der ONSITES Graduate School übertragen?', '<p>Ja, nat&uuml;rlich! Fr&uuml;here Studienleistungen, die an anderen Bildungseinrichtungen erbracht wurden, k&ouml;nnen f&uuml;r Doktoratsstudieng&auml;nge an der ONSITES Graduate School anerkannt werden. Vor der Einschreibung pr&uuml;fen wir die entsprechenden Unterlagen, um festzustellen, welche bisherigen Leistungen anerkannt werden k&ouml;nnen.</p>', 'de'),
(171, 202, 'How can I obtain further information about my desired doctoral program?', '<p>For more information about your desired doctoral program, our support team at ONSITES Graduate School is available via email or telephone to assist you. You can also request further information via the contact form.</p>', 'en'),
(172, 202, 'Wie kann ich weitere Informationen über mein gewünschtes Doktoratsprogramm erhalten?', '<p>F&uuml;r weitere Informationen &uuml;ber Dein gew&uuml;nschtes Doktoratsstudium steht Dir unser Support-Team der ONSITES Graduate School per E-Mail oder Telefon zur Verf&uuml;gung. Zudem kannst Du &uuml;ber das Kontaktformular weitere Infos anfordern.</p>', 'de'),
(173, 203, 'How can I enroll in a doctoral program, and which documents do I need to submit?', '<p>To enroll in a doctoral program, you need to submit the enrollment form along with the following documents: identification card or passport, curriculum vitae, graduation diplomas, and any previously obtained diplomas and certificates. Students from the European Union must provide notarized documents, while students from outside the European Union require notarization and an apostille. If additional documents are required, you will be notified accordingly.</p>', 'en'),
(174, 203, 'Wie kann ich mich für ein Doktoratsstudium einschreiben, und welche Unterlagen muss ich einreichen?', '<p>F&uuml;r die Immatrikulation in ein Doktoratsstudium musst Du das Immatrikulationsformular zusammen mit den folgenden Dokumenten einreichen: Personalausweis oder Reisepass, Lebenslauf, Abschlusszeugnisse und alle zuvor erworbenen Zeugnisse und Zertifikate. Studierende aus der Europ&auml;ischen Union m&uuml;ssen notariell beglaubigte Dokumente vorlegen, w&auml;hrend Studierende aus L&auml;ndern au&szlig;erhalb der Europ&auml;ischen Union eine notarielle Beglaubigung und eine Apostille ben&ouml;tigen. Wenn zus&auml;tzliche Dokumente erforderlich sind, erh&auml;ltst Du eine entsprechende Benachrichtigung.</p>', 'de'),
(175, 204, 'When can I start my doctoral program?', '<p>Our doctoral programs can be initiated at any time, starting every 1st of a month.</p>', 'en'),
(176, 204, 'Wann kann ich mein Doktoratsstudium beginnen?', '<p>Unsere Doktoratsprogramme k&ouml;nnen jederzeit begonnen werden, jeweils zum 1. eines Monats.</p>', 'de'),
(177, 205, 'What is the duration of a doctoral program?', '<p>The standard period of study for a doctoral program at ONSITES Graduate School is one to three years.</p>', 'en'),
(178, 205, 'Wie lange dauert ein Doktoratsstudium?', '<p>Die Regelstudienzeit f&uuml;r ein Doktoratsstudium an der ONSITES Graduate School betr&auml;gt ein bis drei Jahre.</p>', 'de'),
(179, 206, 'Can I shorten the duration of my doctoral studies?', '<p>Yes. If you have previous doctoral achievements from other educational institutions, they can be recognized, potentially shortening the standard duration of your doctoral studies.</p>', 'en'),
(180, 206, 'Kann ich die Dauer meines Doktoratsstudiums verkürzen?', '<p>Ja. Wenn Du bereits Leistungen an einer anderen Bildungseinrichtung erbracht hast, k&ouml;nnen diese anerkannt werden, wodurch sich die Regeldauer Deines Doktoratsstudiums verk&uuml;rzen kann.</p>', 'de'),
(181, 207, 'Do I need to undergo selection or admission test procedures to be admitted to doctoral studies?', '<p>Generally, selection or admission test procedures are not required for doctoral programs. However, some programs may involve admission tests, which often include a brief presentation of the proposed subject matter of the doctorate.</p>', 'en'),
(182, 207, 'Muss ich mich einem Auswahl- oder Zulassungstest unterziehen, um zum Doktoratsstudium zugelassen zu werden?', '<p>Im Allgemeinen sind f&uuml;r Doktoratsstudieng&auml;nge keine Auswahl- oder Zulassungstests erforderlich. Einige Programme k&ouml;nnen jedoch Zulassungstests vorsehen, die h&auml;ufig eine kurze Pr&auml;sentation des vorgeschlagenen Promotionsthemas beinhalten.</p>', 'de'),
(183, 208, 'Who supports me with administrative questions during my doctoral studies?', '<p>For administrative inquiries during your doctoral studies, our support team at ONSITES Graduate School is available to assist you via email or telephone.</p>', 'en'),
(184, 208, 'Wer unterstützt mich bei administrativen Fragen während meines Doktoratsstudiums?', '<p>F&uuml;r administrative Fragen w&auml;hrend des Doktoratsstudiums steht Dir unser Support-Team der ONSITES Graduate School per E-Mail oder Telefon zur Verf&uuml;gung.</p>', 'de'),
(185, 209, 'Who provides academic support during my doctoral studies at ONSITES Graduate School?', '<p>Academic support during doctoral studies at ONSITES Graduate School is generally provided by the respective supervisor, who guides and assists you with academic matters.</p>', 'en'),
(186, 209, 'Wer unterstützt mich während meines Doktoratsstudiums an der ONSITES Graduate School?', '<p>Die akademische Betreuung w&auml;hrend des Doktoratsstudiums an der ONSITES Graduate School erfolgt in der Regel durch den jeweiligen Betreuer, der Dich in Deinen akademischen Angelegenheiten anleitet und unterst&uuml;tzt.</p>', 'de'),
(187, 210, 'How should I allocate my study hours per week during my doctoral studies?', '<p>The amount of study hours per week varies for each doctoral student, as learning styles differ. As a general guideline, it is recommended to allocate eight to twelve hours of study per week.</p>', 'en'),
(188, 210, 'Wie sollte ich meine wöchentlichen Studienzeiten während meines Doktoratsstudiums aufteilen?', '<p>Die Anzahl der Studienstunden pro Woche ist f&uuml;r jeden Doktoranden unterschiedlich, da sich die Lernstile unterscheiden. Als allgemeine Richtlinie wird empfohlen, acht bis zw&ouml;lf Stunden pro Woche f&uuml;r das Studium einzuplanen.</p>', 'de'),
(189, 211, 'Can I balance my doctoral studies with my professional commitments?', '<p>At ONSITES Graduate School, we understand the challenges of pursuing doctoral studies while managing professional responsibilities. With our individualized support and flexible study opportunities, you can effectively balance your doctoral studies with your professional activities.</p>', 'en'),
(190, 211, 'Kann ich mein Doktoratsstudium mit meinen beruflichen Verpflichtungen vereinbaren?', '<p>Wir verstehen die Herausforderungen, die ein Doktoratsstudium mit der Bew&auml;ltigung beruflicher Verpflichtungen mit sich bringt. Dank unserer individuellen Unterst&uuml;tzung und flexiblen Studienm&ouml;glichkeiten kannst Du Dein Doktoratsstudium effektiv mit Deinen beruflichen Aktivit&auml;ten vereinbaren.</p>', 'de'),
(191, 212, 'What are the fees associated with a doctoral program at ONSITES Graduate School?', '<p>The fees for doctoral programs at ONSITES Graduate School depend on factors such as the partnership university, and program duration. We ensure full transparency by displaying and bindingly agreeing upon the tuition fees prior to enrollment.</p>', 'en'),
(192, 212, 'Wie hoch sind die Gebühren für ein Doktoratsstudium an der ONSITES Graduate School?', '<p>Die Geb&uuml;hren f&uuml;r Doktorate an der ONSITES Graduate School h&auml;ngen von Faktoren wie der Partnerhochschule und&nbsp;der Programmdauer ab. Wir sorgen f&uuml;r volle Transparenz, indem wir die Studiengeb&uuml;hren vor der Immatrikulation ausweisen und verbindlich festlegen.</p>', 'de'),
(193, 213, 'Are there any additional costs besides program fees for a doctoral program?', '<p>In addition to program fees, there may be additional costs associated with the recognition of previous doctoral achievements. These costs, if applicable, will be fully transparent and agreed upon. Other potential expenses, such as travel costs, internet and telephone expenses, acquisition of additional literature, participation fees for <a href=\"https://graduate.me/en/conferences-workshops\">conferences and workshops</a>, and <a href=\"https://graduate.me/en/book-publishing\">publishing </a>costs, are the responsibility of the doctoral students.</p>', 'en'),
(194, 213, 'Fallen neben den Programmgebühren für ein Doktoratsstudium noch weitere Kosten an?', '<p>Neben den Programmgeb&uuml;hren k&ouml;nnen zus&auml;tzliche Kosten im Zusammenhang mit der Anerkennung fr&uuml;herer Leistungen anfallen. Diese Kosten werden, sofern sie anfallen, vollst&auml;ndig transparent gemacht und vereinbart. Andere m&ouml;gliche Kosten, wie z. B. Reisekosten, Internet- und Telefonkosten, Anschaffung zus&auml;tzlicher Literatur, Teilnahmegeb&uuml;hren f&uuml;r <a href=\"https://graduate.me/de/konferenzen-und-workshops\">Konferenzen und Workshops</a> sowie Publikationskosten, sind von den Doktoranden selbst zu tragen.</p>', 'de'),
(195, 214, 'In which languages can I pursue a doctoral program at ONSITES Graduate School?', '<p>At ONSITES Graduate School, doctoral programs are conducted in English and German.</p>', 'en'),
(196, 214, 'In welchen Sprachen kann ich ein Doktoratsstudium an der ONSITES Graduate School absolvieren?', '<p>An der ONSITES Graduate School werden die Doktorate in englischer und&nbsp;deutscher Sprache angeboten.</p>', 'de'),
(197, 215, 'Are doctoral studies completed abroad recognized as equivalent to doctoral programs in my home country?', '<p>Yes. If a university is recognized by the relevant authorities in the country of residence and the chosen doctoral programs are accredited, doctoral studies completed abroad hold equivalent recognition on an international scale.</p>', 'en'),
(198, 215, 'Wird ein im Ausland absolviertes Doktoratsstudium als gleichwertig mit einem Doktoratsstudium in meinem Heimatland anerkannt?', '<p>Ja. Wenn eine Hochschule von den zust&auml;ndigen Beh&ouml;rden des Wohnsitzlandes anerkannt ist und das gew&auml;hlte Doktoratsstudium akkreditiert ist, ist ein im Ausland absolviertes Doktoratsstudium international gleichwertig anerkannt.</p>', 'de'),
(199, 216, 'What are the career prospects after successfully completing a doctoral program at ONSITES Graduate School?', '<p>Completing a doctoral program at ONSITES Graduate School opens up excellent career prospects, often surpassing those of conventional study programs. In OECD countries, the average percentage of academics holding a doctoral degree is 2%. Graduates with a doctoral degree in fields such as business, medicine, social science, healthcare, and engineering enjoy particularly promising career opportunities.</p>', 'en'),
(200, 216, 'Wie sind die Berufsaussichten nach einem erfolgreichen Abschluss eines Doktoratsstudiums an der ONSITES Graduate School?', '<p>Der Abschluss eines Doktoratsstudiums an der ONSITES Graduate School er&ouml;ffnet hervorragende Berufsschancen, die oft &uuml;ber die eines herk&ouml;mmlichen Studiengangs hinausgehen. In den OECD-L&auml;ndern liegt der durchschnittliche Prozentsatz der Akademiker mit einem Doktor-Titel bei 2 %. Besonders gute Karrierechancen haben Absolventen eines Doktoratsstudiums in Bereichen wie Wirtschaft, Medizin, Sozialwissenschaften, Gesundheitswesen und Ingenieurwesen.</p>', 'de');
INSERT INTO `financings` (`id`, `text`, `text_de`, `program_id`) VALUES
(1, '<h2 style=\"text-align:center\"><strong>Smart &amp; Flexible: How to finance your Bachelor Professional</strong></h2>\r\n\r\n<h2 style=\"text-align:center\">&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Your career has potential. And it deserves to grow &ndash; without financial pressure. Whether through installment payments, employer support, or tax benefits: we will show you how to finance your further education smartly. Full control, full transparency &ndash; this is how you invest in your next career move with confidence.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Deduct tuition fees from taxes: In many countries, education costs can be partially or fully claimed for tax purposes. [Learn more]</li>\r\n	<li>&nbsp;Pay tuition fees in installments: Tuition fees can be paid flexibly in monthly installments. [Learn more]</li>\r\n	<li>&nbsp;Tuition support from your employer: Some employers support continuing education for their staff. [Learn more]</li>\r\n	<li>&nbsp;Student loans: In various countries, tuition fees are financed through dedicated loan models provided by banks. [Learn more]</li>\r\n</ul>', '<h2 style=\"text-align:center\"><strong>So finanzierst Du Deinen Wirtschaftsfachwirt &ndash; smart &amp; flexibel</strong></h2>\r\n\r\n<h2 style=\"text-align:center\">&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Deine Karriere hat Potenzial. Und das darf wachsen - ohne finanziellen Druck. Ob in Raten, mit Arbeitgeberzuschuss oder steuerlichem Vorteil: Wir zeigen Dir, wie Du Deine Weiterbildung clever finanzierst. Volle Kontrolle, volle Transparenz &ndash; so investierst Du smart in Deinen n&auml;chsten Karriereschritt.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Studiengeb&uuml;hren steuerlich absetzen: In vielen L&auml;ndern k&ouml;nnen Studienkosten ganz oder teilweise steuerlich geltend gemacht werden. [Mehr erfahren]</li>\r\n	<li style=\"text-align:justify\">Studiengeb&uuml;hren in Raten zahlen: Die Studiengeb&uuml;hren k&ouml;nnen flexibel in monatlichen Raten beglichen werden. [Mehr erfahren]</li>\r\n	<li style=\"text-align:justify\">Finanzierung der Studiengeb&uuml;hren durch den Arbeitgeber: Einige Arbeitgeber unterst&uuml;tzen die Weiterbildungen ihrer Mitarbeiter. [Mehr erfahren]</li>\r\n	<li style=\"text-align:justify\">Bildungsdarlehen: In verschiedenen L&auml;ndern werden Studiengeb&uuml;hren durch spezielle Darlehensmodelle von Banken finanziert. [Mehr erfahren]</li>\r\n</ul>', 1),
(2, '<p>asdfasdfdasadsfdasf</p>', '<p>dasfdasfasdasfdsfsd</p>', 15);
