INSERT INTO `general_requests` (`id`, `name`, `email`, `subject`, `request_type`, `message`, `created_at`, `updated_at`) VALUES
(1, 'Schödlbauer', 'a.schoedlbauer@gmx.de', 'Anrechnung', 'Studium verkürzen', 'Sehr geehrte Damen und Herren,\r\n\r\nich habe Interesse an einem Studium im Bereich BWL.\r\n\r\nDieses möchte ich gerne verkürzen.\r\n\r\nIch habe eine Ausbildung als Kaufmann im Gesundheitswesen abgeschlossen. Desweiteren eine IHK-Weiterbildung, genannt Fachberater für Servicemanagement auf DQR Stufe 5.\r\n\r\nWieviele Semester muss ich mit dieser Kombination noch absolvieren?\r\n\r\nVielen Dank,\r\n\r\nMfG Schödlbauer', '2025-03-12 04:38:58', '2025-03-12 04:38:58'),
(2, 'Dimitar Aleksiev', 'dimitaraleksiev135@gmail.com', 'test', 'Coaching', 'test', '2025-03-20 10:07:39', '2025-03-20 10:07:39'),
