INSERT INTO `program_iso_texts` (`id`, `program_id`, `iso_text`, `iso_text_de`) VALUES
(1, 1, '<h2><strong>Accreditations and certifications &ndash; verified quality for your academic journey</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">External quality standards ensure the excellence of ONSITES Graduate School &ndash; both at the institutional level and across all study programs. They reflect a high level of academic integrity and provide you with orientation and confidence.</p>', '<h2><strong>Akkreditierungen und Zertifizierungen: Starke Standards f&uuml;r ein starkes Studium</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Externe Pr&uuml;fstandards sichern die Qualit&auml;t der ONSITES Graduate School &ndash; sowohl auf institutioneller Ebene als auch in allen Studienprogrammen. Sie stehen f&uuml;r ein hohes Anspruchsniveau und geben Dir Orientierung und Vertrauen.</p>'),
