INSERT INTO `program_testimonials` (`id`, `program_id`, `text`, `text_de`, `video`) VALUES
(1, 1, '<h2><strong>Inside ONSITES &ndash; Bachelor Professionals share their story</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Click on the video &amp; get real feedback: Students share why they chose the Bachelor Professional in Business at ONSITES Graduate School, how they experienced their studies, and where they stand today.</p>', '<h2><strong>Inside ONSITES &ndash; Wirtschaftsfachwirte und ihre Story</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>Klick aufs Video &amp; echtes Feedback erfahren: Studierende berichten, warum sie sich f&uuml;r den Wirtschaftsfachwirt an der ONSITES Graduate School entschieden haben, wie sie ihr Studium erlebt haben und wo sie heute stehen.</p>', 'ai-in-education.webp'),
