INSERT INTO `testimonials_translations` (`id`, `name`, `text`, `locale`, `testimonial_id`, `image`) VALUES
(9, 'Jürgen, M.Sc. in IT Management', '<p>&quot;<em>Due to the <strong>high degree of flexibility</strong>, I was able to perfectly balance my studies and career. Recommended to anyone who wants to study at their own pace</em>.&quot;</p>', 'en', 5, 'testimonial.png'),
(10, 'Jürgen, M.Sc. in IT-Management', '<p>&bdquo;<em>Durch die <strong>hohe Flexibilit&auml;t </strong>konnte ich Studium und Beruf perfekt unter einen Hut bringen. Jedem zu empfehlen, der im eigenen Tempo studieren m&ouml;chte</em>.&rdquo;</p>', 'de', 5, 'testimonial.png'),
(13, 'Walter, Doctor of Economics', '<p>&quot;<em>I am glad to have done my doctorate at ONSITES Graduate School. I can hardly imagine a <strong>faster and more convenient way</strong> to get a doctorate - sensational</em>.&quot;</p>', 'en', 7, 'testimonial.png'),
(14, 'Walter, Doctor of Economics', '<p>&bdquo;<em>Ich bin froh, meine Promotion an der ONSITES Graduate School gemacht zu haben. Einen <strong>schnelleren und bequemeren Weg</strong> zum Doktor-Titel kann ich mir kaum vorstellen &ndash; sensationell</em>.&rdquo;</p>', 'de', 7, 'testimonial.png'),
(15, 'Britta, Doctor of Health Sciences', '<p>&quot;<em>What I appreciated the most was the smooth <strong>all-round&nbsp;support</strong>. My personal contact made studying a straightforward experience at every stage</em>.&quot;</p>', 'en', 8, 'testimonial.png'),
(16, 'Britta, Doctor of Health Sciences', '<p>&bdquo;<em>Am meisten sch&auml;tzte ich die reibungslose <strong>Rundum-Betreuung</strong>. Mein pers&ouml;nlicher Ansprechpartner machte das Studium in jeder Phase zu einem unkomplizierten Erlebnis</em>.&quot;</p>', 'de', 8, 'testimonial.png');
