INSERT INTO `tuition_fees` (`id`, `program_id`, `text`, `text_de`) VALUES
(1, 1, '<h2><strong>Bachelor Professional clearly calculated: These are your tuition fees</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Transparency is part of our quality promise. That&rsquo;s why we openly show you all fees &ndash; from application to final assessment. Within the standard duration of study, all fees remain stable. No hidden costs, no unexpected increases. This allows you to plan your studies realistically and with confidence from day one.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Application fee (EU, EEA &amp; Switzerland): 200.00 EUR</li>\r\n	<li>Application fee (non-EU countries): 300.00 EUR</li>\r\n	<li>Enrollment fee (one-time): 300.00 EUR</li>\r\n	<li>Tuition fees (total): 3,600.00 EUR</li>\r\n	<li>Peer review fee (optional): 500.00 EUR</li>\r\n	<li>Shipping fee for certificates: 50.00 EUR</li>\r\n</ul>', '<h2><strong>Wirtschaftsfachwirt klar kalkuliert: Das sind Deine Studiengeb&uuml;hren</strong></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p style=\"text-align:justify\">Transparenz ist Teil unseres Qualit&auml;tsversprechens. Deshalb zeigen wir Dir offen, welche Geb&uuml;hren anfallen &ndash; vom Antrag bis zur Pr&uuml;fung. Innerhalb der Regelstudienzeit sind die Geb&uuml;hren garantiert stabil &ndash; ohne versteckte Kosten oder sp&auml;tere Erh&ouml;hungen. So kannst Du Dein Studium von Anfang an realistisch und sicher planen.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Antragsgeb&uuml;hr (f&uuml;r Bewerber aus EU, EWR &amp; Schweiz): 200,00 EUR</li>\r\n	<li>Antragsgeb&uuml;hr (f&uuml;r Bewerber aus Drittstaaten): 300,00 EUR</li>\r\n	<li>Immatrikulationsgeb&uuml;hr (einmalig): 300,00 EUR</li>\r\n	<li>Studiengeb&uuml;hren (Gesamtbetrag): 3.600,00 EUR</li>\r\n	<li>Peer-Review-Geb&uuml;hr (optional): 500,00 &euro;</li>\r\n	<li>Versandpauschale f&uuml;r Zertifikate: 50,00 EUR</li>\r\n</ul>'),
(2, 15, '<p>asdfasdfasdfdasf</p>', '<p>dasfdasfsdasfadsfa</p>');
